var id = "64a8cc022f143010cada54492799b6da";
var restApi = new Isilon_AWX_RestApi(id);
var jobVal = workflow.scratchpad.jobId;
var url = 'api/v2/jobs/'+ jobVal + '/stdout/';
var jsonTry =  new global.JSON();
var rest = restApi.RESTCall(url, "get", "");
var pattern = /FAILED(.*)false(.*)msg\\": (.*)}\\/;
var output = JSON.parse(rest.responseBody);
var qry = JSON.stringify(output);
if(qry.match(pattern)) {
	var results = qry.match(pattern);
	var msg = results[3];
	msg = msg.replace(/\\u001b\[0m\\n\\u001b\[0;31m/, "");
    workflow.scratchpad.errormsg = msg;
 }
else{
  var mesg = "Check for the error msg in AWX manually";
  workflow.scratchpad.errormsg = mesg;
}
gs.info("Blah blah::: " + workflow.scratchpad.errormsg);

