var id = "64a8cc022f143010cada54492799b6da";
var restApi = new Isilon_AWX_RestApi(id);
var url = 'api/v2/job_templates/9/launch/';
var jsonTry =  new global.JSON();

var AWX_ShareName = current.variables.SNOW_ShareName;
var AWX_TopLevelDir = current.variables.SNOW_TopLevelDir;
var AWX_Protocol = current.variables.SNOW_Protocol;
var AWX_SizeinGB = current.variables.SNOW_SizeinTB*1024;
var AWX_LocalSnapshotRequired = current.variables.SNOW_LocalSnapshotRequired;
var AWX_ReplicationRequired = current.variables.SNOW_ReplicationRequired;
if (AWX_ReplicationRequired == 'Yes') {
   gs.info("AWX_ReplicationRequired will always be False.");
   AWX_ReplicationRequired = "No";
}
var posixOwner = current.variables.SNOW_PosixOwner;
var posixGroup = current.variables.SNOW_PosixGroup;
var posixEveryone = current.variables.SNOW_PosixEveryone;
var AWX_PosixMode = posixOwner + posixGroup + posixEveryone;
gs.info("AWX_PosixMode:: " + AWX_PosixMode);
var AWX_StorageTier = current.variables.SNOW_StorageTier;
var AWX_Owner = current.variables.SNOW_Owner;
var AWX_GroupOwner = current.variables.SNOW_GroupOwner;

gs.info("Checking...");
var payLo = '{"extra_vars": { "AWX_ShareName":"' + AWX_ShareName + '","AWX_UseCustomCredentials":"Yes","AWX_TopLevelDir":"' + AWX_TopLevelDir + '","AWX_Protocol":"' + AWX_Protocol + '","AWX_AccessLevelOwner":"' + AWX_PosixMode + '","AWX_CapacityinGB":' + AWX_SizeinGB + ',"AWX_LocalSnapshotRequired":"' + AWX_LocalSnapshotRequired + '","AWX_StorageTier":"' + AWX_StorageTier + '","AWX_Owner":"' + AWX_Owner + '","AWX_GroupOwner":"' + AWX_GroupOwner + '","AWX_ReplicationRequired":"' + AWX_ReplicationRequired + '" } }';
gs.info("payLo value:" + payLo);

var payload = jsonTry.decode(payLo);
var rest = restApi.RESTCall(url, "post", payload);
var res = JSON.parse(rest.responseBody);
var jobId = res['job'];
workflow.scratchpad.jobId = jobId;

