var id = "e2546fe207ba5010f4e6ff4c7c1ed093";
var restApi = new Isilon_AWX_RestApi(id);
var url = 'api/v2/job_templates/42/launch/';
var jsonTry =  new global.JSON();

var gqd = new getQuotaDetails();
var isilon_type = current.variables.isilon_type;
var quota_path = current.variables.quota_path;
var val = {};
if(isilon_type == 'NFS') {
	var res_nfs = gqd.getNfsQuotaDetails(quota_path);
	val = JSON.parse(res_nfs);
}
if(isilon_type == 'SMB') {
	var res_smb = gqd.getSmbQuotaDetails(quota_path);
	val = JSON.parse(res_smb);
}
var physical_path = val.physical_path;
var srcarray = val.cluster;
var AccessZone = val.access_zone;
var CapacityinGB = current.variables.enter_the_quota_size;
var serviceCenterImplFlag = "true";
var CheckArrayUtilization = "No";

gs.info("Checking...");
var payLo = '{"extra_vars": { "physical_path":"' + physical_path + '","serviceCenterImplFlag":"' + serviceCenterImplFlag + '","srcarray":"' + srcarray + '","AccessZone":"' + AccessZone + '","CapacityinGB":' + CapacityinGB + ',"CheckArrayUtilization":"' + CheckArrayUtilization + '" } }';
gs.info("payLo value:" + payLo);

var payload = jsonTry.decode(payLo);
var rest = restApi.RESTCall(url, "post", payload);
var res = JSON.parse(rest.responseBody);
var jobId = res['job'];
workflow.scratchpad.jobId = jobId;

